(230, '_site_transient_otw_upd_plug', 'O:8:\"stdClass\":2:{s:12:\"last_updated\";i:1554217172;s:4:\"data\";a:1:{s:32:\"435d5ae8b3c3a3f701359e9db268d339\";a:13:{s:7:\"version\";s:4:\"1.14\";s:3:\"key\";b:0;s:4:\"mode\";s:0:\"\";s:4:\"slug\";s:25:\"tabs-shortcode-and-widget\";s:8:\"settings\";a:3:{s:11:\"menu_parent\";s:16:\"otw-tsw-settings\";s:7:\"lc_name\";s:15:\"License Manager\";s:8:\"menu_key\";s:7:\"otw-tsw\";}s:6:\"domain\";s:19:\"localhost/wordpress\";s:4:\"path\";s:49:\"tabs-shortcode-and-widget/otw_content_manager.php\";s:2:\"ip\";s:3:\"::1\";s:2:\"id\";s:32:\"435d5ae8b3c3a3f701359e9db268d339\";s:9:\"multisite\";b:0;s:7:\"domains\";a:1:{i:0;s:19:\"localhost/wordpress\";}s:4:\"dnms\";b:0;s:4:\"info\";a:11:{s:5:\"valid\";s:3:\"yes\";s:8:\"messages\";a:1:{i:0;a:5:{s:5:\"title\";s:7:\"Buy Pro\";s:4:\"type\";s:7:\"message\";s:5:\"stype\";s:2:\"pp\";s:4:\"text\";s:704:\"<i class=\"otw-factory-icon fa fa-arrow-circle-o-up\"></i><br />\r\n<div class=\"otw-factory-rcol\"><h3 class=\"otw_factory_head\">#plugin_name# - Online Documentation</h3><div class=\"otw_factory_body\">Check online documentation for the Lite version.</div><div class=\"otw_factory_action\"><a href=\"#lite_doc_url#\"  target=\"_blank\">Lite Documentation</a></div><br />\r\n<h3 class=\"otw_factory_head\">Upgrade to Pro version</h3><div class=\"otw_factory_body\">Upgrade to Pro version to get premium support and the full list of features.</div><div class=\"otw_factory_action\"><a href=\"#pro_plugin_url#\" class=\"otw_factory_button_action button button-primary\" target=\"_blank\">Buy Pro license</a></div><br />\r\n</div><br />\r\n\";s:4:\"vars\";a:3:{s:11:\"plugin_name\";s:26:\"Tabs Shortcode And Widgets\";s:12:\"lite_doc_url\";s:144:\"http://otwthemes.com/online-documentation-tabs-shortcode-and-widget-plugin/?utm_source=wp.org&utm_medium=admin&utm_content=docs&utm_campaign=cml\";s:14:\"pro_plugin_url\";s:150:\"https://codecanyon.net/item/content-manager-for-wordpress/7431829?ref=OTWthemes&utm_sourc